import React, { useState, useContext, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import './HomePage.css';

function HomePage() {
  const { login, user, isLoading, loginError } = useContext(AuthContext);
  const navigate = useNavigate();

  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [rememberMe, setRememberMe] = useState(true);
  const [error, setError] = useState('');
  
  // Efecto para redireccionar si el usuario ya está autenticado
  useEffect(() => {
    if (user) {
      // Si el usuario ya está logueado, redirigir según su rol
      if (user.rol === 'admin') {
        navigate('/usuarios');
      } else if (user.rol === 'jugador') {
        navigate('/mis-metricas');
      }
    }
  }, [user, navigate]);

  // Efecto para manejar errores de login desde el contexto
  useEffect(() => {
    if (loginError) {
      setError(loginError);
    }
  }, [loginError]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    
    if (!email || !password) {
      setError('Por favor, complete todos los campos');
      return;
    }
    
    try {
      await login(email, password, rememberMe);
      // El redireccionamiento se maneja en el primer useEffect cuando cambia "user"
    } catch (err) {
      console.error("Error en login:", err);
      setError('Credenciales incorrectas. Por favor, inténtalo de nuevo.');
    }
  };

  // Función para manejar la navegación a diferentes secciones
  const scrollToSection = (sectionId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <div className="home-page">
      <header className="home-header">
        <div className="logo">Lionta F.C.</div>
        <nav className="main-nav">
          <ul>
            <li><a href="#inicio" onClick={(e) => { e.preventDefault(); scrollToSection('inicio'); }}>Inicio</a></li>
            <li><a href="#programas" onClick={(e) => { e.preventDefault(); scrollToSection('programas'); }}>Programas</a></li>
            <li><a href="#horarios" onClick={(e) => { e.preventDefault(); scrollToSection('horarios'); }}>Horarios</a></li>
            <li><a href="#contacto" onClick={(e) => { e.preventDefault(); scrollToSection('contacto'); }}>Contacto</a></li>
            {user?.rol === 'admin' && (
              <li><a href="/usuarios" style={{color: 'var(--accent-color)'}}>Panel Admin</a></li>
            )}
            {user?.rol === 'jugador' && (
              <li><a href="/mis-metricas" style={{color: 'var(--accent-color)'}}>Mis Métricas</a></li>
            )}
          </ul>
        </nav>
      </header>
      
      <main className="hero-section" id="inicio">
        <div className="hero-content">
          <div className="hero-text">
            <h1>Forma parte de nuestra familia futbolística</h1>
            <p>Desarrolla tu talento con los mejores entrenadores y metodología de primera clase.</p>
            <div className="hero-buttons">
              {user ? (
                <button className="btn btn-primary" onClick={() => 
                  user.rol === 'admin' ? navigate('/usuarios') : navigate('/mis-metricas')
                }>
                  Mi Dashboard
                </button>
              ) : (
                <a href="#login-section" className="btn btn-primary" onClick={(e) => {
                  e.preventDefault();
                  document.querySelector('.login-card').scrollIntoView({ behavior: 'smooth' });
                }}>
                  Comenzar ahora
                </a>
              )}
              <button className="btn btn-outline" onClick={() => scrollToSection('programas')}>
                Saber más
              </button>
            </div>
          </div>
          
          {/* Mostramos el formulario de login solo si el usuario no está autenticado */}
          {!user && (
            <div className="login-card" id="login-section">
              <div className="login-form-container">
                <h2>Accede a tu cuenta</h2>
                {error && <div className="error-message">{error}</div>}
                <form className="login-form" onSubmit={handleSubmit}>
                  <div className="form-group">
                    <label htmlFor="email">Correo electrónico</label>
                    <input
                      type="email"
                      id="email"
                      placeholder="tucorreo@ejemplo.com"
                      value={email}
                      onChange={(e) => setEmail(e.target.value)}
                      required
                    />
                  </div>
                  
                  <div className="form-group">
                    <label htmlFor="password">Contraseña</label>
                    <input
                      type="password"
                      id="password"
                      placeholder="Tu contraseña"
                      value={password}
                      onChange={(e) => setPassword(e.target.value)}
                      required
                    />
                  </div>
                  
                  <div className="form-options">
                    <div className="remember-me">
                      <input
                        type="checkbox"
                        id="remember"
                        checked={rememberMe}
                        onChange={(e) => setRememberMe(e.target.checked)}
                      />
                      <label htmlFor="remember">Recordarme</label>
                    </div>
                    <a href="/recuperar-contrasena" className="forgot-password">
                      ¿Olvidaste?
                    </a>
                  </div>
                  
                  <button 
                    type="submit" 
                    className="btn btn-login"
                    disabled={isLoading}
                  >
                    {isLoading ? 'Cargando...' : 'Iniciar sesión'}
                  </button>
                </form>
              </div>
            </div>
          )}
          
          {/* Si el usuario está autenticado, mostramos un mensaje o panel */}
          {user && (
            <div className="welcome-panel">
              <h2>¡Bienvenido, {user.nombre || user.email}!</h2>
              <p>Accede a tu panel personalizado para ver tus datos y actividades.</p>
              {user.rol === 'admin' ? (
                <div className="admin-quick-links">
                  <a href="/usuarios" className="btn btn-primary">Lista de Usuarios</a>
                  <a href="/registro" className="btn btn-outline">Crear Usuario</a>
                  <a href="/metricas" className="btn btn-outline">Ver Métricas</a>
                  <a href="/ver-estadisticas" className="btn btn-outline">Ver Estadísticas</a>
                </div>
              ) : user.rol === 'jugador' ? (
                <a href="/mis-metricas" className="btn btn-primary">Ver mis métricas</a>
              ) : (
                <a href="/" className="btn btn-primary">Dashboard</a>
              )}
            </div>
          )}
        </div>
        
        <div className="hero-features">
          <div className="hero-feature">
            <h3>Entrenadores Profesionales</h3>
            <p>Nuestro equipo de entrenadores certificados asegura un desarrollo técnico y táctico óptimo.</p>
          </div>
          
          <div className="hero-feature">
            <h3>Instalaciones de Primera</h3>
            <p>Contamos con campos de entrenamiento de alta calidad y equipamiento moderno.</p>
          </div>
          
          <div className="hero-feature">
            <h3>Metodología Única</h3>
            <p>Nuestro programa formativo integral combina aspectos técnicos, físicos y valores deportivos.</p>
          </div>
        </div>
      </main>
      
      {/* Secciones adicionales para la navegación */}
      <section id="programas" className="section">
        <div className="container">
          <h2>Nuestros Programas</h2>
          <p>Información detallada sobre nuestros programas de entrenamiento.</p>
          {/* Contenido de programas aquí */}
        </div>
      </section>
      
      <section id="horarios" className="section">
        <div className="container">
          <h2>Horarios</h2>
          <p>Horarios de entrenamiento y partidos del club.</p>
          {/* Contenido de horarios aquí */}
        </div>
      </section>
      
      <section id="contacto" className="section">
        <div className="container">
          <h2>Contacto</h2>
          <p>Información de contacto y ubicación del club.</p>
          {/* Formulario de contacto aquí */}
        </div>
      </section>
      
      <footer className="home-footer">
        <p>&copy; {new Date().getFullYear()} Lionta F.C. Todos los derechos reservados.</p>
      </footer>
    </div>
  );
}

export default HomePage;
